import React from 'react';
import {
  List,
  ListItem,
  ListItemText,
  ListItemSecondaryAction,
  IconButton,
  Typography,
  Paper,
  Divider,
  Box,
  Chip
} from '@mui/material';
import { format } from 'date-fns';
import { ja } from 'date-fns/locale';
import EditIcon from '@mui/icons-material/Edit';
import DeleteIcon from '@mui/icons-material/Delete';

const ScheduleList = ({ scheduleData, onEdit, onDelete }) => {
  // 日付でグループ化
  const groupedSchedules = scheduleData.reduce((groups, schedule) => {
    const date = format(new Date(schedule.date), 'yyyy-MM-dd');
    if (!groups[date]) {
      groups[date] = [];
    }
    groups[date].push(schedule);
    return groups;
  }, {});

  // 日付でソート
  const sortedDates = Object.keys(groupedSchedules).sort();

  return (
    <Paper 
      elevation={0}
      sx={{ 
        borderRadius: 2,
        overflow: 'hidden'
      }}
    >
      {sortedDates.map((date, index) => (
        <Box key={date}>
          <Box sx={{ 
            p: 2, 
            bgcolor: 'background.default',
            borderBottom: '1px solid',
            borderColor: 'divider'
          }}>
            <Typography variant="subtitle1" sx={{ fontWeight: 'bold' }}>
              {format(new Date(date), 'M月d日 (EEEE)', { locale: ja })}
            </Typography>
          </Box>
          <List>
            {groupedSchedules[date].map((schedule, scheduleIndex) => (
              <React.Fragment key={scheduleIndex}>
                <ListItem
                  sx={{
                    py: 2,
                    '&:hover': {
                      bgcolor: 'action.hover',
                    },
                  }}
                >
                  <ListItemText
                    primary={
                      <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
                        <Typography variant="subtitle1">
                          {schedule.eventName}
                        </Typography>
                        {schedule.reservationStatus && (
                          <Chip
                            label={schedule.reservationStatus}
                            size="small"
                            color="primary"
                            variant="outlined"
                          />
                        )}
                      </Box>
                    }
                    secondary={
                      <Box sx={{ mt: 0.5 }}>
                        <Typography variant="body2" color="text.secondary">
                          {schedule.time && `${schedule.time} - `}
                          {schedule.location}
                        </Typography>
                        {schedule.memo && (
                          <Typography variant="body2" color="text.secondary" sx={{ mt: 0.5 }}>
                            {schedule.memo}
                          </Typography>
                        )}
                        {schedule.budget && (
                          <Typography variant="body2" color="text.secondary" sx={{ mt: 0.5 }}>
                            予算: ¥{Number(schedule.budget).toLocaleString()}
                          </Typography>
                        )}
                      </Box>
                    }
                  />
                  <ListItemSecondaryAction>
                    <IconButton 
                      edge="end" 
                      aria-label="edit"
                      onClick={() => onEdit(schedule)}
                      sx={{ mr: 1 }}
                    >
                      <EditIcon />
                    </IconButton>
                    <IconButton 
                      edge="end" 
                      aria-label="delete"
                      onClick={() => onDelete(schedule)}
                    >
                      <DeleteIcon />
                    </IconButton>
                  </ListItemSecondaryAction>
                </ListItem>
                {scheduleIndex < groupedSchedules[date].length - 1 && <Divider />}
              </React.Fragment>
            ))}
          </List>
        </Box>
      ))}
    </Paper>
  );
};

export default ScheduleList;
